mapper.update:
UPDATE rboard
	SET
		rudate = systimestamp,
	  rcontent = #{rcontent}
	WHERE  rnum = #{rnum}

mapper.insert:
INSERT INTO rboard (
  rnum, 
  rid, 		 bnum, 	 rname, 	 rcontent, 		rgroup,
  rindent, 				rstep, 		 prnum, 	 prid,	 prname
) VALUES (
  RBOARD_RNUM_SEQ.nextval, 
  #{rid}, #{bnum}, #{rname}, #{rcontent}, #{rgroup},
  #{rindent}+1, #{rstep}+1, #{prnum}, #{prid}, #{prname}	
)

mapper.update:
UPDATE rboard
	SET rstep = rstep + 1
	WHERE rgroup = #{rgroup} 
	AND rstep > #{rstep}

mapper.select:
SELECT rid, bnum, rname, rgroup, rindent, rstep
	FROM rboard
	WHERE rnum = #{rnum}
